import React from "react";
import Form from "../components/Form";
import Results from "../components/Results";
import API from "../utils/API";

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bookTitle: "",
      bookInfo: []
    }
  }

  componentDidMount() {

}

  handleChange = event => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSearch = event => {
    event.preventDefault();
    API.searchBooks(this.state.bookTitle)
      .then(
        (response) => {
          this.setState({ bookTitle: "", bookInfo: response.data })
      }
    )
  }

  render() {
    return (
      <main>
        <Form handleChange={this.handleChange} handleSearch={this.handleSearch} />

        {(this.state.bookInfo.length > 0) ?
          <Results bookInfo={this.state.bookInfo} path={this.props.match.path} /> : null
        }
      </main>
    )
  }
}

export default Search;
