import React from "react";
import Form from "../components/Form";
import Results from "../components/Results";
import API from "../utils/API";

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputTitle: "",
      bookData: []
    }
  }

  handleChange = event => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSearch = event => {
    event.preventDefault();
    API.searchBooks(this.state.inputTitle)
      .then(
        (response) => {
          this.setState({ inputTitle: "", bookInfo: response.data })
      }
    )
  }

  render() {
    return (
      <main>
        <Form handleChange={this.handleChange} handleSearch={this.handleSearch} />

        {(this.state.bookData.length > 0) ?
          <Results bookData={this.state.bookData} path={this.props.match.path} /> : null
        }
      </main>
    )
  }
}

export default Search;
