import axios from "axios";
require("dotenv").config();

const API_KEY = process.env.REACT_APP_GOOGLE_BOOKS_API_KEY;

export default {
  // Gets all books
  getBooks: function() {
    return axios.get("/api/books");
  },
  // Gets the book with the given id
  // getBook: function(id) {
  //   return axios.get("/api/books/" + id);
  // },
  // finds all books with searched title from google books
  searchBooks: function (title) {
    return axios.get("/search", (req, res) => {
      // take user searched title replace white space with + for the url search to work properly
      let bookTitle = req.body.title.replace(/\s/g, "+");
      axios.get("https://www.googleapis.com/books/v1/volumes?q=" + bookTitle + "&key=" + API_KEY)
        .then(
          (response) => {
            res.json(response.data.items)
          })
        .catch (
          (error) => {
            res.json({error: error})
          })
      })
  },
  // Saves a book to the database
  saveBook: function(bookData) {
    return axios.post("/api/books", bookData);
  },
  // Deletes the book with the given id
  deleteBook: function(id) {
    return axios.delete("/api/books/" + id);
  }
};
